
public class OperadoresLogicos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// e lógico
		System.out.println(2 > 0 && 3 < 7);
		// sem curto circuito
		System.out.println(2 > 0 & 3 < 7);
		System.out.println(1 & 3);
		
		// ou lógico
		System.out.println(2 > 10 || 3 == 5);
		// sem curto circuito
		System.out.println(2 > 10 | 3 == 5);
		System.out.println(1 | 3);
		
		
		// não lógico
		
		
		

	}

}
