
public class EstruturaDeRepeticaoFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Inicialização
		//Condição
		//Incremento
		for(int x = 1; x <= 10; x++) {
			System.out.println(x);
		}
		
		int k;
		for(k = 10; k >= 0; k--) {
			System.out.println(k);
		}
        System.out.println(k);
	}

}
